import {React, useState, useEffect} from "react";
import '../css/style.css'

const Notes = props => props.data.map(note => <div>{note.text}</div>);

function Tools()
{
    var [data,newData]  = useState([]);
    var [dataisloaded,changeState] = useState(false);
    useEffect(() => {
        fetch("https://api.openweathermap.org/data/3.0/onecall?lat=33.44&lon=-94.04&exclude=daily&appid=91ff1e3cfbab6d1f933dd9af24f4229c")
                        .then((res) => res.json())
                        .then((json) => {
                            newData(()=>json)
                            changeState(()=> true)
                        });
        }, []);

    const initialData = [{ text: 'Hey' }, { text: 'There' }];
    const [data1, setData1] = useState(initialData);


    const handleClick = () => {
        const text = document.querySelector('#noteinput').value.trim();
        if (text) {
          const nextState = produce(data, draftState => {
            draftState.push({ text });
          });
          document.querySelector('#noteinput').value = '';
          setData(nextState);
        }
      };


    return(
    <>
    <br></br>
    <div className="heading">Weather API</div>
        <div className="details">
            <div className="form-center">
                <h5>Current time is :{JSON.stringify(data)} </h5>
            </div>
        </div>

        <br></br>
    <div className="heading">Weather API</div>
        <div className="details">
            <div className="form-center">
            <input id="noteinput" style={{ width: '80%' }} type="text" placeholder="Enter a new note" />
        <button onClick={() => handleClick()}>Add note</button>
        <Notes data={data1} />
            </div>
        </div>



    </>
    )

}

export default Tools;
